import React, { useState } from 'react';
import Chat from './components/Chat';
import ChatWidget from './components/ChatWidget';

function App() {
  const [showWidget, setShowWidget] = useState(false);

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="container mx-auto px-4 py-8">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold text-gray-800">
            RAG Chatbot
          </h1>
          <div className="flex space-x-4">
            <button
              onClick={() => setShowWidget(false)}
              className={`px-4 py-2 rounded-lg transition-all duration-200 ${
                !showWidget 
                  ? 'bg-[#0a392c] text-white' 
                  : 'bg-white text-[#0a392c] border border-[#0a392c] hover:bg-[#0a392c] hover:text-white'
              }`}
            >
              Main Chat
            </button>
            <button
              onClick={() => setShowWidget(true)}
              className={`px-4 py-2 rounded-lg transition-all duration-200 ${
                showWidget 
                  ? 'bg-[#0a392c] text-white' 
                  : 'bg-white text-[#0a392c] border border-[#0a392c] hover:bg-[#0a392c] hover:text-white'
              }`}
            >
              Widget Mode
            </button>
          </div>
        </div>
        
        {!showWidget ? (
          <Chat />
        ) : (
          <div className="text-center">
            <div className="bg-white rounded-lg shadow-lg border border-gray-100 p-8">
              <h2 className="text-2xl font-semibold text-gray-800 mb-4">
                Widget Mode Active
              </h2>
              <p className="text-gray-600 mb-6">
                The chat widget is now active. Look for the chat button in the bottom-right corner of the screen.
              </p>
              <div className="inline-block bg-gray-100 rounded-lg p-4">
                <div className="w-16 h-16 rounded-full bg-[#0a392c] text-white shadow-lg flex items-center justify-center text-2xl mx-auto mb-2">
                  ðŸ’¬
                </div>
                <p className="text-sm text-gray-600">Chat button appears here</p>
              </div>
            </div>
          </div>
        )}
      </div>
      
      {/* Always render the widget when in widget mode */}
      {showWidget && <ChatWidget />}
    </div>
  );
}

export default App;
